--
-- Name: check_level_with_parent(integer, uuid); Type: FUNCTION; Schema: tenant_nadeem; Owner: -
--

CREATE FUNCTION tenant_nadeem.check_level_with_parent(current_level integer, old_parent_location_id uuid) RETURNS boolean
    LANGUAGE plpgsql
    AS $$
      BEGIN
        IF old_parent_location_id IS NULL THEN
          RETURN TRUE; -- No parent, so the check passes
        END IF;

        RETURN current_level > COALESCE(
          (SELECT l.level FROM "tenant_nadeem"."locations" l WHERE l.id = old_parent_location_id),
          -1
        );
      END;
      $$;


--
-- Name: check_journal_entry_balanced(); Type: FUNCTION; Schema: tenant_power_electronics; Owner: -
--

CREATE FUNCTION tenant_power_electronics.check_journal_entry_balanced() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
        DECLARE
            total_debit NUMERIC;
            total_credit NUMERIC;
        BEGIN
            SELECT SUM(debit_amount), SUM(credit_amount)
            INTO total_debit, total_credit
            FROM "tenant_power_electronics"."account_journal_items"
            WHERE journal_entry_id = NEW.journal_entry_id and deleted_at is NULL;
  
            IF total_debit IS DISTINCT FROM total_credit THEN
                RAISE EXCEPTION 'Journal entry % is not balanced: total debit = %, total credit = %',
                                NEW.journal_entry_id, total_debit, total_credit;
            END IF;
  
            RETURN NULL;
        END;
        $$;


