--
-- Name: vw_journal_view; Type: VIEW; Schema: tenant_power_electronics; Owner: -
--

CREATE VIEW tenant_power_electronics.vw_journal_view AS
 SELECT je.id,
    je.created_at,
    je.updated_at,
    je.date,
    je.reference_type,
    je.reference_id,
    je.journal_entry_number,
    je.journal_id,
    je.partner_type,
    je.partner_id,
    je.status,
    je.creator_id,
    COALESCE(ct.amount, (0)::numeric) AS amount,
    j.name AS journal_name
   FROM ((tenant_power_electronics.account_journal_entries je
     LEFT JOIN ( SELECT aji.journal_entry_id,
            sum(aji.debit_amount) AS amount
           FROM tenant_power_electronics.account_journal_items aji
          GROUP BY aji.journal_entry_id) ct ON ((ct.journal_entry_id = je.id)))
     JOIN tenant_power_electronics.account_journals j ON ((j.id = je.journal_id)))
  WHERE (je.deleted_at IS NULL);


--
-- Name: vw_assets; Type: VIEW; Schema: tenant_power_electronics; Owner: -
--

CREATE VIEW tenant_power_electronics.vw_assets AS
 SELECT a.id,
    a.created_at,
    a.updated_at,
    a.deleted_at,
    a.material_id,
    mat.name,
    a.asset_tag,
    a.serial,
    a.acquisition_date,
    a.original_value,
    a.not_depreciable_value,
    a.status,
    a.in_service_date,
    a.creator_id,
    a.asset_model_id,
    COALESCE(je_sum.total_amount, (0)::numeric) AS sum_of_posted_journal_entries,
    (COALESCE(je_sum.total_amount, (0)::numeric) + COALESCE(aa.depreciated_amount, (0)::numeric)) AS depreciated_value,
    (((COALESCE(a.original_value, (0)::numeric) - COALESCE(a.not_depreciable_value, (0)::numeric)) - COALESCE(aa.depreciated_amount, (0)::numeric)) - COALESCE(je_sum.total_amount, (0)::numeric)) AS depreciable_value,
    ((((COALESCE(a.original_value, (0)::numeric) - COALESCE(a.not_depreciable_value, (0)::numeric)) - COALESCE(aa.depreciated_amount, (0)::numeric)) - COALESCE(je_sum.total_amount, (0)::numeric)) + COALESCE(a.not_depreciable_value, (0)::numeric)) AS book_value
   FROM ((((tenant_power_electronics.assets a
     LEFT JOIN tenant_power_electronics.asset_depreciations ad ON ((a.id = ad.asset_id)))
     LEFT JOIN tenant_power_electronics.asset_accounts aa ON ((a.id = aa.asset_id)))
     LEFT JOIN tenant_power_electronics.materials mat ON ((a.material_id = mat.id)))
     LEFT JOIN ( SELECT jv.reference_id,
            sum(jv.amount) AS total_amount
           FROM tenant_power_electronics.vw_journal_view jv
          WHERE (((jv.status)::text = 'posted'::text) AND ((jv.reference_type)::text = 'asset'::text))
          GROUP BY jv.reference_id) je_sum ON ((a.id = je_sum.reference_id)))
  WHERE (a.deleted_at IS NULL);


--
-- Name: vw_invoice_view; Type: VIEW; Schema: tenant_power_electronics; Owner: -
--

CREATE VIEW tenant_power_electronics.vw_invoice_view AS
 WITH invoice_base AS (
         SELECT ai.invoice_number,
            ai.issued_date,
            ai.id AS invoice_id,
            c.name AS customer_name,
            c.id AS company_id,
            COALESCE((items.sub_total + ((COALESCE(charges.total_surcharges, (0)::numeric) / 100.0) * items.sub_total)), (0)::numeric) AS total,
            (ai.status)::text AS original_status,
            ai.created_at,
            ai.updated_at,
                CASE
                    WHEN (apt.due_in_days IS NOT NULL) THEN (ai.issued_date + ((apt.due_in_days || ' days'::text))::interval)
                    WHEN ((apt.code)::text = 'EOM'::text) THEN ((date_trunc('month'::text, ai.issued_date) + '1 mon'::interval) - '1 day'::interval)
                    WHEN ((apt.code)::text = 'NET30EOM'::text) THEN (((date_trunc('month'::text, ai.issued_date) + '1 mon'::interval) - '1 day'::interval) + '30 days'::interval)
                    ELSE NULL::timestamp without time zone
                END AS due_date,
            COALESCE(payment_invoice.paid, (0)::numeric) AS paid_amount
           FROM (((((tenant_power_electronics.account_invoices ai
             JOIN tenant_power_electronics.companies c ON ((ai.company_id = c.id)))
             JOIN tenant_power_electronics.account_payment_terms apt ON ((apt.id = ai.payment_terms_id)))
             LEFT JOIN ( SELECT account_invoice_charges.invoice_id,
                    sum(account_invoice_charges.value) AS total_surcharges
                   FROM tenant_power_electronics.account_invoice_charges
                  WHERE (account_invoice_charges.is_active = true)
                  GROUP BY account_invoice_charges.invoice_id) charges ON ((charges.invoice_id = ai.id)))
             LEFT JOIN ( SELECT computed_items.invoice_id,
                    sum(((computed_items.base_amount - computed_items.discount_amount) + computed_items.tax_on_discount)) AS sub_total
                   FROM ( SELECT account_invoice_items.invoice_id,
                            ((account_invoice_items.quantity)::numeric * account_invoice_items.price_per_uom) AS base_amount,
                            ((account_invoice_items.discount / 100.0) * ((account_invoice_items.quantity)::numeric * account_invoice_items.price_per_uom)) AS discount_amount,
                            ((account_invoice_items.tax / 100.0) * (((account_invoice_items.quantity)::numeric * account_invoice_items.price_per_uom) - ((account_invoice_items.discount / 100.0) * ((account_invoice_items.quantity)::numeric * account_invoice_items.price_per_uom)))) AS tax_on_discount
                           FROM tenant_power_electronics.account_invoice_items) computed_items
                  GROUP BY computed_items.invoice_id) items ON ((items.invoice_id = ai.id)))
             LEFT JOIN ( SELECT api.invoice_id,
                    sum(api.amount) AS paid
                   FROM (tenant_power_electronics.account_payment_invoices api
                     JOIN tenant_power_electronics.account_payments ap ON ((api.payment_id = ap.id)))
                  GROUP BY api.invoice_id) payment_invoice ON ((payment_invoice.invoice_id = ai.id)))
          WHERE (ai.deleted_at IS NULL)
        )
 SELECT invoice_number,
    issued_date,
    invoice_id,
    customer_name,
    total,
    due_date,
    created_at,
    updated_at,
        CASE
            WHEN ((original_status <> ALL (ARRAY['DRAFT'::text, 'PAID'::text])) AND ((due_date)::date < CURRENT_DATE)) THEN 'OVERDUE'::text
            ELSE original_status
        END AS status,
    paid_amount,
    company_id,
    COALESCE((total - paid_amount), (0)::numeric) AS remaining_amount
   FROM invoice_base;


