-- Name: vw_sales_order_view; Type: VIEW; Schema: tenant_power_electronics; Owner: -
--

CREATE VIEW tenant_power_electronics.vw_sales_order_view AS
 WITH item_calc AS (
         SELECT sales_order_items.sales_order_id,
            ((sales_order_items.quantity)::numeric * sales_order_items.price_per_uom) AS base_amount,
            (COALESCE(sales_order_items.discount, (0)::numeric) / (100)::numeric) AS dis,
            (COALESCE(sales_order_items.taxes, (0)::numeric) / (100)::numeric) AS tax
           FROM tenant_power_electronics.sales_order_items
        ), order_calc AS (
         SELECT item_calc.sales_order_id,
            sum(((item_calc.base_amount - (item_calc.base_amount * item_calc.dis)) + ((item_calc.base_amount - (item_calc.base_amount * item_calc.dis)) * item_calc.tax))) AS grand_total
           FROM item_calc
          GROUP BY item_calc.sales_order_id
        )
 SELECT so.id,
    so.order_date,
    so.sales_order_number,
    so.company_id,
    so.assigned_to,
    so.created_at,
    so.updated_at,
    so.contact_id,
    so.status,
    COALESCE(oc.grand_total, (0)::numeric) AS total
   FROM (tenant_power_electronics.sales_orders so
     LEFT JOIN order_calc oc ON ((oc.sales_order_id = so.id)));


