-- Name: vw_vendor_bill_view; Type: VIEW; Schema: tenant_power_electronics; Owner: -
--

CREATE VIEW tenant_power_electronics.vw_vendor_bill_view AS
 WITH vendor_bill_base AS (
         SELECT vb.id AS vendor_bill_id,
            vb.accounting_date,
            vb.bill_due_date,
            vb.company_id,
            vb.notes,
            vb.journal_entry_id,
            vb.bill_number,
            vb.bill_date,
            c.name AS vendor_name,
            COALESCE(items.sub_total, (0)::numeric) AS amount,
            COALESCE(payments.paid_amount, (0)::numeric) AS paid_amount,
            (vb.status)::text AS original_status,
            vb.created_at,
            vb.updated_at,
            vb.bill_reference
           FROM (((tenant_power_electronics.account_vendor_bills vb
             JOIN tenant_power_electronics.companies c ON ((vb.company_id = c.id)))
             LEFT JOIN ( SELECT account_vendor_bill_items.vendor_bill_id,
                    sum((((account_vendor_bill_items.quantity)::numeric * account_vendor_bill_items.price_per_uom) + ((account_vendor_bill_items.tax / 100.0) * ((account_vendor_bill_items.quantity)::numeric * account_vendor_bill_items.price_per_uom)))) AS sub_total
                   FROM tenant_power_electronics.account_vendor_bill_items
                  GROUP BY account_vendor_bill_items.vendor_bill_id) items ON ((items.vendor_bill_id = vb.id)))
             LEFT JOIN ( SELECT apb.bill_id,
                    sum(apb.amount) AS paid_amount
                   FROM (tenant_power_electronics.account_payment_bills apb
                     JOIN tenant_power_electronics.account_payments ap ON ((apb.payment_id = ap.id)))
                  GROUP BY apb.bill_id) payments ON ((payments.bill_id = vb.id)))
          WHERE (vb.deleted_at IS NULL)
        )
 SELECT vendor_bill_id AS id,
    accounting_date,
    bill_due_date,
    company_id,
    notes,
    journal_entry_id,
    bill_number,
    bill_date,
    vendor_name,
    amount,
    bill_reference,
    created_at,
    updated_at,
        CASE
            WHEN ((original_status <> ALL (ARRAY['DRAFT'::text, 'PAID'::text])) AND (bill_due_date IS NOT NULL) AND (bill_due_date < CURRENT_DATE)) THEN 'OVERDUE'::text
            ELSE original_status
        END AS status,
    paid_amount,
    COALESCE((amount - paid_amount), (0)::numeric) AS remaining_amount
   FROM vendor_bill_base;


